    package com.example.Testing;

import java.util.*;

class Hotel {
    private String name;
    private List<Room> rooms;

    public Hotel(String name, List<Room> rooms) {
        this.name = name;
        this.rooms = rooms;
    }

    public String getName() {
        return name;
    }

    public List<Room> getRooms() {
        return rooms;
    }
}

class Room {
    private String number;
    private boolean isAvailable;

    public Room(String number) {
        this.number = number;
        this.isAvailable = true;
    }

    public String getNumber() {
        return number;
    }

    public boolean isAvailable() {
        return isAvailable;
    }

    public void setAvailable(boolean available) {
        isAvailable = available;
    }
}

class Reservation {
    private String customerName;
    private Room room;

    public Reservation(String customerName, Room room) {
        this.customerName = customerName;
        this.room = room;
        room.setAvailable(false);
    }

    public String getCustomerName() {
        return customerName;
    }

    public Room getRoom() {
        return room;
    }
}

public class Main {
    public static void main(String[] args) {
        List<Room> rooms = new ArrayList<>();
        for (int i = 1; i <= 10; i++) {
            rooms.add(new Room("Room " + i));
        }

        Hotel hotel = new Hotel("The Grand Hotel", rooms);

        Scanner scanner = new Scanner(System.in);
        while (true) {
            System.out.println("Welcome to " + hotel.getName() + ". Please choose an option:");
            System.out.println("1. Check room availability");
            System.out.println("2. Make a reservation");
            System.out.println("3. Exit");

            int option = scanner.nextInt();
            scanner.nextLine(); // consume newline

            if (option == 1) {
                System.out.println("Available rooms:");
                for (Room room : hotel.getRooms()) {
                    if (room.isAvailable()) {
                        System.out.println(room.getNumber());
                    }
                }
            } else if (option == 2) {
                System.out.println("Enter your name:");
                String name = scanner.nextLine();

                System.out.println("Enter room number:");
                String roomNumber = scanner.nextLine();

                Room roomToReserve = null;
                for (Room room : hotel.getRooms()) {
                    if (room.getNumber().equals(roomNumber) && room.isAvailable()) {
                        roomToReserve = room;
                        break;
                    }
                }

                if (roomToReserve != null) {
                    Reservation reservation = new Reservation(name, roomToReserve);
                    System.out.println("Reservation made for " + reservation.getCustomerName() + " in " + reservation.getRoom().getNumber());
                } else {
                    System.out.println("Room not available.");
                }
            } else if (option == 3) {
                System.out.println("Thank you for using our system. Goodbye!");
                break;
            } else {
                System.out.println("Invalid option. Please try again.");
            }
        }

        scanner.close();
    }
}
